import { Button, Grid } from "@mui/material";
import "@splidejs/splide/dist/css/splide.min.css";
import "bootstrap/dist/css/bootstrap.min.css";
import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import SwiperCore, {
  A11y,
  Autoplay,
  Navigation,
  Pagination,
  Scrollbar
} from "swiper";
import "swiper/css";
import "swiper/css/autoplay";
import "swiper/css/navigation";
import "swiper/css/pagination";
import "swiper/css/scrollbar";
import { Swiper, SwiperSlide } from "swiper/react";
import { locationData } from "../../../data";
import background from '../../../Rectangle 1.png';
import Items from "../Item/Items";
import "./Home.css";
const Home = () => {
  SwiperCore.use([Autoplay]);
  let navigate = useNavigate();
  const [product, setProduct] = useState("");
  const handleClick = (id) => {
    navigate(`/details/${id}`);
  };
  return (
    <div style={{backgroundImage:`url(${background})`,backgroundPosition:'center'}}>
      <div style={{width:'80%', margin:'auto'}}>
        <Grid container spacing={4}>
          <Grid item xs={12} sm={5} >
            <div style={{fontSize:'60px', fontWeight:'bold'}}>{locationData[product]?.name}</div>
            <p>{locationData[product]?.desc.slice(0,100)}...</p>
            <Button variant="contained" >
            <Link  to={`/details/${product + 1}`}>
             Explore
            </Link>
            </Button>
            
          </Grid>
          <Grid item xs={12} sm={7}>
            <Swiper
              navigation
              modules={[Navigation, Pagination, Scrollbar, A11y]}
              autoplay={{ delay: 2000, disableOnInteraction: true }}
              pagination={{ clickable: true }}
              scrollbar={{ draggable: true }}
              spaceBetween={20}
              slidesPerView={1}
              onSlideChange={(e) => setProduct(e.activeIndex)}
              onSwiper={(swiper) => setProduct(swiper.activeIndex)}
            >
              {locationData.map((item) => (
                <SwiperSlide key={Math.random()}>
                  <Items item={item} handleClick={handleClick} />
                </SwiperSlide>
              ))}
            </Swiper>
          </Grid>
        </Grid>
      </div>
    </div>
  );
};

export default Home;
