import { Box } from "@mui/material";
import React from "react";
import "swiper/css";

const Item = ({ item, handleClick }) => {
  // setArr(item);
  // arr==='center'? setProduct(item): null;
  return (
    <div onClick={() => {handleClick(item.id)}} >
      <Box
      sx={{
        width: 300,
        height: 300,
        backgroundColor: 'red',
        '&:hover': {
          backgroundColor: 'primary.main',
          opacity: [0.9, 0.8, 0.7],
        },
      }}
    >

      <img height={100}  src={item.img} alt="" />
    </Box>
    </div>
  );
};

export default Item;
