import "@splidejs/splide/dist/css/splide.min.css";
import "bootstrap/dist/css/bootstrap.min.css";
import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import SwiperCore, {
  A11y,
  Autoplay,
  Navigation,
  Pagination,
  Scrollbar
} from "swiper";
import "swiper/css";
import "swiper/css/autoplay";
import "swiper/css/navigation";
import "swiper/css/pagination";
import "swiper/css/scrollbar";
import { Swiper, SwiperSlide } from "swiper/react";
import { locationData } from "../../../data";
import Item from "../Item/Item";
import "./Home.css";

const Home = () => {
  SwiperCore.use([Autoplay]);
  let navigate = useNavigate();
  const [product, setProduct] = useState("");
  const handleClick = (id) => {
    navigate(`/details/${id}`);
  };
  return (
    <div >
      
      <div>{locationData[product]?.name}</div>
      <Link to={`/details/${product + 1}`}>
        <button>explore</button>
      </Link>
      <Swiper
        navigation
        modules={[Navigation, Pagination, Scrollbar, A11y]}
        autoplay={{ delay: 5000, disableOnInteraction: true }}
        pagination={{ clickable: true }}
        scrollbar={{ draggable: true }}
        spaceBetween={20}
        slidesPerView={1}
        onSlideChange={(e) => setProduct(e.activeIndex)}
        onSwiper={(swiper) => setProduct(swiper.activeIndex)}
      >
        {locationData.map((item) => (
          <SwiperSlide style={{background:'red'}} key={Math.random()}>
            <Item item={item} handleClick={handleClick} />
          </SwiperSlide>
        ))}
      </Swiper>
    </div>
  );
};

export default Home;
